function FindClass_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilebutton_15': 'FindClass_mobilebutton_15',
        'mobilebutton_9': 'FindClass_mobilebutton_9',
        'mobilebutton_10': 'FindClass_mobilebutton_10',
        'mobilenavbar_13': 'FindClass_mobilenavbar_13',
        'mobilenavbaritem_14': 'FindClass_mobilenavbaritem_14'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'FindClass';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var FindClass_onLoad = function() {
            FindClass_elementsExtraJS();

            FindClass_deviceEvents();
            FindClass_windowEvents();
            FindClass_elementsEvents();
        };

    // screen window events


    function FindClass_windowEvents() {

        $('#FindClass').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function FindClass_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function FindClass_elementsExtraJS() {
        // screen (FindClass) extra code

    };

    // screen elements handler


    function FindClass_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#FindClass_mobileheader [name="mobilebutton_15"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Home', {
                        reverse: false
                    });

                }
            },
        }, '#FindClass_mobileheader [name="mobilebutton_15"]');

        $(document).off("click", '#FindClass_mobilecontainer [name="mobilebutton_9"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('MCampus', {
                        reverse: false
                    });

                }
            },
        }, '#FindClass_mobilecontainer [name="mobilebutton_9"]');
        $(document).off("click", '#FindClass_mobilecontainer [name="mobilebutton_10"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('ClassroomAvailability', {
                        reverse: false
                    });

                }
            },
        }, '#FindClass_mobilecontainer [name="mobilebutton_10"]');

        $(document).off("click", '#FindClass_mobilefooter [name="mobilenavbaritem_14"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Home', {
                        reverse: false
                    });

                }
            },
        }, '#FindClass_mobilefooter [name="mobilenavbaritem_14"]');

    };

    $(document).off("pagebeforeshow", "#FindClass").on("pagebeforeshow", "#FindClass", function(event, ui) {
        Apperyio.CurrentScreen = "FindClass";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    FindClass_onLoad();
};

